import React, { useContext } from "react";
import TemplateContext from "../contexts/TemplateContext";
import templates from "./../config/templates.json";

function PolicySelection() {
  const { currentTemplate, setTheCurrentTemplate } =
    useContext(TemplateContext);

  const onClick = (e) => {
    if (e.target.value === 0) {
      setTheCurrentTemplate("select a template...");
    } else {
      setTheCurrentTemplate(e.target.value);
      console.log(e.target.value + " was clicked!");
    }
  };

  const getTemplates = () => {
    templates.map((template) => {
      for (const [templateName, parametersObject] of Object.entries(template)) {
        console.log(templateName);
        return <option value={templateName}>{templateName}</option>;
      }
    });
  };

  return (
    <div>
      <select
        className="dropdown"
        id="templates"
        value={currentTemplate}
        onChange={onClick}
      >
        <option value="0">Select a template...</option>
        {getTemplates()}
      </select>
    </div>
  );
}

export default PolicySelection;
