import React, { useContext } from "react";
import TemplateContext from "./contexts/TemplateContext";
import PolicySelection from "./components/PolicySelection";
import Obligation from "./components/Obligation";
import { v4 } from "uuid";

function AppComponent() {
  const { currentTemplate } = useContext(TemplateContext);

  const template1 = {
    openIdToken: { idClaims: "", accessClaims: "" },
    externalVCIssuer: { resourceUrl: "" },
  };

  const template2 = {
    openIdToken: { idClaims: "", accessClaims: "" },
    compliance: { complianceLevel: "" },
    locationServices: { locationProvider: "" },
  };

  const selectTemplateToDisplay = () => {
    displayListOfObligations(
      currentTemplate === "template1" ? template1 : template2
    );
  };

  const displayListOfObligations = (templateObject) => {
    let obligations = [];
    for (const [obligationName, parametersObject] of Object.entries(
      templateObject
    )) {
      let parameters = [];
      for (const [parameterName, parameterValue] of Object.entries(
        parametersObject
      )) {
        parameters.push(parameterName);
      }
      obligations.push(
        <Obligation
          key={v4()}
          obligationName={obligationName}
          parameters={parameters}
        />
      );
    }

    return obligations;
  };

  return (
    <div className="App">
      <header className="App-header">
        <PolicySelection />
        <ul>{selectTemplateToDisplay()}</ul>
        <h1 className="text-sm font-bold underline text-red-800">
          {currentTemplate}
        </h1>
      </header>
    </div>
  );
}

export default AppComponent;
