import React, { useContext } from "react";
import TemplateContext from "./contexts/TemplateContext";
import PolicySelection from "./components/PolicySelection";
import Obligation from "./components/Obligation";
import { v4 } from "uuid";
import templatesFromJson from "./config/templates.json";

function AppComponent() {
  const { currentTemplate } = useContext(TemplateContext);

  const template1 = {
    openIdToken: { idClaims: "", accessClaims: "" },
    externalVCIssuer: { resourceUrl: "" },
  };

  const template2 = {
    openIdToken: { idClaims: "", accessClaims: "" },
    compliance: { complianceLevel: "" },
    locationServices: { locationProvider: "" },
  };

  const selectTemplateToDisplay = () => {
    return displayListOfObligations(
      currentTemplate === "template1" ? template1 : template2
    );
  };

  const displayListOfObligations = (templateObject) => {
    let obligations = [];

    // get TEMPLATE list as objects
    templatesFromJson.map((template) => {
      for (const [templateObject, templateValue] of Object.entries(template)) {
        // get TEMPLATE NAMES
        for (const [templateName, parametersObject] of Object.entries(
          templateObject
        )) {
          // get OBLIGATIONS names
          for (const [obligationName, obligationValue] of Object.entries(
            templateName
          )) {
            // get PARAMETERS names
            let parameters = [];
            for (const [parameterName, parameterValue] of Object.entries(
              obligationName
            )) {
              parameters.push(parameterName);
            }
          }
          obligations.push(
            <Obligation
              key={v4()}
              obligationName={obligationName}
              parameters={parameters}
            />
          );
        }
      }
    });

    return obligations;
  };

  const onClickJson = (event) => {
    // get template names list
    templates.map((template) => {
      for (const [templateName, parametersObject] of Object.entries(template)) {
        console.log(templateName);
      }
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <button type="button" className="" onClick={onClickJson}>
          get json file
        </button>
        <PolicySelection />
        <ul>{selectTemplateToDisplay()}</ul>
      </header>
    </div>
  );
}

export default AppComponent;
