import React, { useContext } from "react";
import TemplateContext from "./contexts/TemplateContext";
import PolicySelection from "./components/PolicySelection";
import Obligation from "./components/Obligation";
import { v4 } from "uuid";

function AppComponent() {
  const { currentTemplate } = useContext(TemplateContext);

  const displayListOfObligations = () => {
    let obligations = [];
    for (let index = 0; index < 6; index++) {
      obligations.push(<Obligation key={v4()} number={index} />);
    }
    return obligations;
  };

  return (
    <div className="App">
      <header className="App-header">
        <PolicySelection />
        <ul>{displayListOfObligations()}</ul>
        <h1 className="text-sm font-bold underline text-red-800">
          {currentTemplate}
        </h1>
      </header>
    </div>
  );
}

export default AppComponent;
